mapper.select:
SELECT
    *
FROM
    patient
WHERE
    create_time BETWEEN #{startDate} AND #{endDate}
ORDER BY
    ${column} ${order}
LIMIT
    #{from}, #{size}

SELECT
    *
FROM
    patient
WHERE
    name = #{keyword} or patient_id = #{keyword}
ORDER BY
    ${column} ${order}
LIMIT
    #{from}, #{size}

mapper.update:
UPDATE
    patient
SET
    slice_state = #{sliceState}, model_id = #{modelId}, check_time = CURRENT_TIMESTAMP, check_uid = #{checkUid}
WHERE
    slice_id = #{sliceId}
